import React, { useState } from "react";
import plane from "../asset/plane.png";
import usa from "../asset/usa.png";
import euro from "../asset/eur.png";
import uk from "../asset/uk.png";
import cad from "../asset/cad.png";
import aud from "../asset/aud.png";
import jpy from "../asset/jpy.png";
import inr from "../asset/inr.png";
import naira from "../asset/naira.png";
import vector from "../asset/Vector.png";
import plug from "../asset/plug.png";

const countries = [
  { code: "USD", name: " - US Dollar", flag: usa },
  { code: "NGN", name: " - Nigerian Naira", flag: naira },
  { code: "EUR", name: " - Euro", flag: euro },
  { code: "GBP", name: " - Bristish Pound", flag: uk },
  { code: "CAD", name: " - Canadian Dollar", flag: cad },
  { code: "AUD", name: " - Australian Dollar", flag: aud },
  { code: "JPY", name: " - Japanese Yen", flag: jpy },
  { code: "INR", name: " - Indian Rupee", flag: inr },
];

const Hero = () => {
  const [amount, setAmount] = useState("");
  const [fromCountry, setFromCountry] = useState(countries[0].code);
  const [toCountry, setToCountry] = useState(countries[1].code);
  const [showToDropdown, setShowToDropdown] = useState(false);

  const toggleToDropdown = () => {
    setShowToDropdown(!showToDropdown);
  };

  return (
    <div className="bg-[#054B98] flex flex-col items-center justify-center h-full relative">
      <img src={plane} alt="" className="h-auto max-w-full mb-8" />
      <div className="flex flex-col items-center gap-3 w-full px-4 md:px-8 lg:px-16">
        <h1 className="font-mont text-[#FDF9F9] font-extrabold text-3xl md:text-5xl lg:text-7xl leading-normal text-center">
          A BETTER WAY TO FLY PRIVATE
        </h1>
        <div className="flex flex-col md:flex-row gap-3">
          <button className="bg-[#FFB01D] font-mont px-4 py-2 rounded-full w-full md:w-[180px] h-[57px] font-bold text-lg">
            EXCHANGE
          </button>
          <button className="bg-[#FFF] font-mont px-4 py-2 rounded-full w-full md:w-[180px] h-[57px] font-bold text-lg">
            READ MORE
          </button>
        </div>
      </div>
      <div className="rounded-lg w-full md:w-[90%] bg-white shadow-md flex flex-col justify-center items-center p-4 md:p-8 lg:p-12 absolute bottom-8">
        <div className="flex flex-col md:flex-row gap-8 mb-4">
          <div className="flex flex-col w-full md:w-[322px]">
            <label className="block text-lg font-bold text-[#000] font-mont">
              Amount
            </label>
            <input
              type="text"
              id="amount"
              placeholder="N 150,000"
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-indigo-500 focus:border-indigo-500"
            />
          </div>
          <div className="flex flex-col w-full md:w-[322px]">
            <label className="block text-lg font-bold text-[#000] font-mont" htmlFor="toCountry">
              From:
            </label>
            {/* ... (your existing code) ... */}
          </div>
          <img src={plug} alt="plug" className="w-16 h-16 md:w-[79px] md:h-[79px] self-end" />
          <div className="flex flex-col w-full md:w-[322px]">
            <label className="block text-lg font-bold text-[#000] font-mont" htmlFor="toCountry">
              To:
            </label>
            {/* ... (your existing code) ... */}
          </div>
        </div>
        <div className="w-full flex justify-end">
          <button className="rounded-3xl bg-[#054B98] p-4 text-[#FFF] font-mont">
            Convert
          </button>
        </div>
      </div>
    </div>
  );
};

export default Hero;
