import React, { useState } from 'react';
import logo from "../asset/areocovert-logo.svg";

const Navbar = () => {
  const [isMobileMenuOpen, setMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <div className="bg-[#fff] flex justify-evenly h-[104px] items-center fixed top-0 left-0 w-full z-10 ">
      <div className="">
        <img src={logo} alt="logo" />
      </div>
      <div className="flex">
        <ul className="flex justify-between gap-[40px]">
          <li className="text-[#054B98] font-mont text-base font-semibold normal-case">
            Home
          </li>
          <li className="text-[#054B98] font-mont text-base font-semibold normal-case">
            About
          </li>
          <li className="text-[#054B98] font-mont text-base font-semibold normal-case">
            Service
          </li>
          <li className="text-[#054B98] font-mont text-base font-semibold normal-case">
            Contact
          </li>
        </ul>
      </div>
      <div className=" flex gap-7 ">
        <button
          className=" text-[#054B98] font-mont text-base font-semibold normal-case "
          onClick={() => {
            window.location.href = "/login";
          }}
        >
          Login
        </button>
        <button
          className="bg-[#FFB01D] font-mont text-[#000] px-4 py-2 rounded-full text-lg font-bold leading-normal capitalize w-[180px] h-[50px]"
          onClick={() => {
            window.location.href = "/register";
          }}
        >
          sign up
        </button>
      </div>
    </div>
  );
};

export default Navbar;
