import React, { useEffect, useState } from "react";
import logo from "../asset/areocovert-logo.svg";
import { Link, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import Spinner from "../component/Spinner";
import { LogOut } from "../utils/firebase/AuthFirestore";
import { observer } from "mobx-react-lite";
import { auth } from "../firebase/firebase";

// console.log()
const Navbar = () => {
  const [currentUser, setCurrentUser] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  // const navigate = useNavigate();

  useEffect(() => {
    setCurrentUser(window.sessionStorage.getItem("user") as string);
  }, [auth.currentUser]);

  const handleSignOut = async (e: any) => {
    e.preventDefault();
    setLoading(true);
    const res = await LogOut();
    if (res.error === false) {
      setTimeout(() => setLoading(false), 1000);
      toast.success("Logout Successful!");
      // setTimeout(() => navigate("/login"), 1000);
    } else {
      setLoading(false);
      toast.error("Logout Failed!");
    }
  };
  return (
    <div className="bg-[#fff] flex justify-evenly h-[104px] items-center fixed top-0 left-0 w-full z-10 ">
      {loading && <Spinner />}

      <div className="">
        <img src={logo} alt="logo" />
      </div>
      <div className="flex">
        <ul className="flex justify-between gap-[40px]">
          <li className="text-[#054B98] font-mont text-base font-semibold normal-case">
            Home
          </li>
          <li className="text-[#054B98] font-mont text-base font-semibold normal-case">
            About
          </li>
          <li className="text-[#054B98] font-mont text-base font-semibold normal-case">
            Service
          </li>
          <li className="text-[#054B98] font-mont text-base font-semibold normal-case">
            Contact
          </li>
        </ul>
      </div>
      {!currentUser ? (
        <div className="flex gap-7 ">
          <Link
            to="/login"
            className="px-1 pt-4 text-[#054B98] font-mont text-base font-semibold normal-case"
          >
            Login
          </Link>

          <Link to="/register">
            <button className="bg-[#FFB01D] font-mont text-[#000] px-4 py-2 rounded-full text-lg font-bold leading-normal capitalize w-[180px] h-[50px]">
              sign up
            </button>
          </Link>
        </div>
      ) : (
        <div className="flex gap-7 ">
          <Link
            to="/dashboard"
            className="px-1 pt-4 text-[#054B98] font-mont text-base font-semibold normal-case"
          >
            Dashboard
          </Link>
          <button
            className="bg-[#FFB01D] font-mont text-[#000] px-4 py-2 rounded-full text-lg font-bold leading-normal capitalize w-[180px] h-[50px]"
            onClick={handleSignOut}
          >
            Sign out
          </button>
        </div>
      )}
    </div>
  );
};

export default observer(Navbar);
